import { ChartConfig } from '../../../utils';
import { ModelTypes } from '../../interfaces/common';
/**
 * Payload for model config change event.
 */
export type ModelConfigChangePayload<T extends ModelTypes = ModelTypes> = {
    /**
     * Name of the function that was called which triggered the change.
     */
    functionCalled: string;
    /**
     * New chart config.
     */
    config: ChartConfig<T>;
};
