import { ChartConfig } from '../../utils';
import { IFeatureLayer, SupportedLayer, WebChartBarChartSeries, WebChartLineChartSeries, WebChartRadarChartSeries } from '@arcgis/charts-spec';
import { ModelTypes } from './common';
/**
 * Parameters for creating a chart model.
 */
export type ModelParams<T extends ChartConfig> = {
    /**
     * Layer instance associated with the chart.
     */
    layer?: SupportedLayer;
    /**
     * Layer description to be used when the `layer` property is not set.
     */
    iLayer?: IFeatureLayer;
    /**
     * Chart config object to be edited. If not provided, a new config will be created using default values.
     */
    config?: T;
};
/**
 * Type alias for the supported serial chart config.
 */
export type SerialChartConfig = ChartConfig<typeof ModelTypes.BarChart> | ChartConfig<typeof ModelTypes.ComboBarLineChart> | ChartConfig<typeof ModelTypes.LineChart> | ChartConfig<typeof ModelTypes.RadarChart>;
/**
 * Type alias for the supported serial chart series.
 */
export type SerialChartSeries = WebChartBarChartSeries | WebChartLineChartSeries | WebChartRadarChartSeries;
