import { IFeatureLayer, WebChart, WebChartDataTypes, WebChartNullPolicyTypes, WebChartSeries, WebChartSeriesQuery, WebChartSortOrderKinds, WebChartTemporalSeries, WebChartText, WebChartTimeAggregationTypes } from '@arcgis/charts-spec';
export type FormerWebChartSubtitle = WebChart & {
    subTitle?: WebChartText;
};
export type FormerWebChartSeriesQuery = WebChartSeriesQuery & {
    orderByFields?: string[];
};
export declare enum FormerWebChartLabelBehavior {
    NONE = "none",
    ROTATE = "rotate",
    STAGGER = "stagger",
    WRAP = "wrap"
}
export type FormerWebChartBoxPlotDataSortTypes = "category" | "mean" | "median";
export type FormerWebChartDataValueTypes = "label" | "originalValue";
export type FormerWebChartWithSortLabelsBy = WebChart & {
    sortLabelsBy?: FormerWebChartDataValueTypes;
};
export type FormerWebChartWithSortSeriesBy = WebChart & {
    sortOptions?: {
        sortSeriesBy?: {
            sortType?: FormerWebChartDataValueTypes;
            sortOrder?: WebChartSortOrderKinds;
        };
    };
};
export type FormerWebChartWithSortDataBy = WebChart & {
    sortOptions?: {
        sortDataBy?: {
            sortType: FormerWebChartBoxPlotDataSortTypes;
            sortOrder?: WebChartSortOrderKinds;
        };
    };
};
export type FormerWebChartWithDataPreferDataLabel = WebChart & {
    orderOptions?: {
        data?: {
            preferDataLabel?: boolean;
        };
    };
};
export type FormerWebChartWithDataPreferLabel = WebChart & {
    orderOptions?: {
        data?: {
            preferLabel?: boolean;
        };
    };
};
/**
 * JSON representation of the inline data source
 */
export interface FormerWebChartInlineDataSource {
    /**
     * Represents a unique string identifier for the type of chart data source. It is **inline** for inline data (arrays of POJOs).
     */
    type: "inline";
    /**
     * Indicates whether the data is already processed and be used right away. If it is `false`, then the data needs to go through some transformation before being assigned to chart/series.
     */
    processed: boolean;
    /**
     * Structure containing all the data for the chart.
     */
    data: WebChartDataTypes;
}
/**
 * JSON representation of the feature layer data source
 */
export interface FormerWebChartLayerDataSource {
    /**
     * Represents a unique string identifier for the type of chart data source.
     */
    type: "layer";
    /**
     * Carries the properties necessary to build a JSAPI Feature Layer in order to fetch the chart data.
     */
    layer: IFeatureLayer;
}
export interface FormerInlineData<T extends FormerWebChartInlineDataSource["data"]> {
    /**
     * Structure containing all the data for the chart. It can be pre-processed (see `WebChartDataTypes`) or not (WebChartDataItem[])
     */
    inputData: T;
    /**
     * Indicates whether the data is already processed and be used right away. If it is `false`, then the data needs to go through some transformation before being assigned to chart/series.
     */
    processed: FormerWebChartInlineDataSource["processed"];
}
export type FormerWebChartTemporalSeries = WebChartSeries & WebChartTemporalSeries & {
    timeIntervalUnits?: "esriTimeUnitsDays" | "esriTimeUnitsHours" | "esriTimeUnitsMinutes" | "esriTimeUnitsMonths" | "esriTimeUnitsSeconds" | "esriTimeUnitsWeeks" | "esriTimeUnitsYears";
    timeIntervalSize?: number;
    timeAggregationType?: WebChartTimeAggregationTypes;
    trimIncompleteTimeInterval?: boolean;
    nullPolicy?: WebChartNullPolicyTypes;
};
